public void testFromJson() throws IOException {     String json = "{
" + "  "span_within" : {
" + "    "big" : {
" + "      "span_near" : {
" + "        "clauses" : [ {
" + "          "span_term" : {
" + "            "field1" : {
" + "              "value" : "bar",
" + "              "boost" : 1.0
" + "            }
" + "          }
" + "        }, {
" + "          "span_term" : {
" + "            "field1" : {
" + "              "value" : "baz",
" + "              "boost" : 1.0
" + "            }
" + "          }
" + "        } ],
" + "        "slop" : 5,
" + "        "in_order" : true,
" + "        "boost" : 1.0
" + "      }
" + "    },
" + "    "little" : {
" + "      "span_term" : {
" + "        "field1" : {
" + "          "value" : "foo",
" + "          "boost" : 1.0
" + "        }
" + "      }
" + "    },
" + "    "boost" : 2.0
" + "  }
" + "}".     SpanWithinQueryBuilder parsed = (SpanWithinQueryBuilder) parseQuery(json).     checkGeneratedJson(json, parsed).     assertEquals(json, "foo", ((SpanTermQueryBuilder) parsed.littleQuery()).value()).     assertEquals(json, 2, ((SpanNearQueryBuilder) parsed.bigQuery()).clauses().size()).     assertEquals(json, 2.0, parsed.boost(), 0.0). }

